import styled from 'styled-components';

export const StyledCard = styled.div`
    display: flex;
    /* flex-direction: row; */
    padding: 60px;
    align-items:center;
    box-shadow: 0 0 10px rgba(0,0,0,0.15);
    /* margin-bottom: 20px; */
    margin: 40px 40px;
    border-radius: 15px;
    background-color: #fff;

    img{
        width:40%;
    }

`
